mapper.insert:
    insert into member
        (id, pw, name, email)
    values
	      (
	        #{id}
	        , #{pw}
	        , #{name}
	        , #{email}
	        , #{addr}
	        , #{birthdate}
	        , #{hp}
	      )

mapper.update:
    update member set
	      reg_Dt = now()

	        ,name = #{name}


	        ,email = #{email}


	        ,addr = #{addr}


	        ,birthdate = #{birthdate}


	        ,hp = #{hp}

    where
        id = #{id}
